    state={
        uploadedFiles:[]
    };

    handleUpload = files =>{
        console.log(files)
    }

    render() {
        return (
            <div>
            <form action="http://localhost:3001/posts" method="POST" encType="multipart/form-data" onUpload={this.handleUpload} >
                <h1>File Upload</h1>
                <input type="file" name="file"  />
                <button type="submit">Upload</button>
            </form>
            <img src=""/>
            </div>
        )
    }
}

export default ReactUploadImage; 


