import { useRef } from 'react';
import React,{useState, useEffect} from 'react'
import Api from '../../../services/api';

const URL_API = 'http://localhost:3001';

function ReactUploadImage() {

  const filesElement = useRef(null);
  const [imageSrc, setImage] = useState('');

  function saveImg(){
    var xabula =imageSrc.value;
    localStorage.setItem('xabula', xabula) 
  }

  const sendFile = async (e) => {
      e.preventDefault(); 
      //localStorage.getItem('welcome-app/username', imageSrc);
      const user = await JSON.parse(localStorage.getItem('user', imageSrc));
      //window.location.reload();

    const dataForm = new FormData();
    for (const file of filesElement.current.files) {
      dataForm.append('file', file);
    }
    //dataForm.append('nomeUsuario', 'João');

    const res = await fetch(`${URL_API}/posts`, {
      method: 'POST',
      body: dataForm,
    });
    const data = await res.json();

    setImage(URL_API + data.caminho)
    //console.log(imageSrc);
    //console.log(xabula);
    
  
  };


//let pessoa = {nome: 'Matheus', idade: 29}

// Transformar o objeto em string e salvar em localStorage
//localStorage.setItem('img', JSON.stringify(imageSrc));

// Receber a string
//let pessoaString = localStorage.getItem('img');

// transformar em objeto novamente
//let pessoaObj = JSON.parse(pessoaString);

/* console.log(pessoaString); // Matheus

localStorage.setItem("key_da_propriedade",imageSrc);
 */




  return (
    
    <div >
      <input type="file" multiple ref={filesElement}  
              onChange={e => setImage(e.target.value)}  />
      <button onClick={sendFile}>Send file</button>
      <button onClick={saveImg}>Xabula</button>
      <img src={imageSrc}/>
      <strong>{imageSrc}</strong>
    </div>
    
  );
}

export default ReactUploadImage;
