import React, {useState, useEffect} from 'react';
import axios from 'axios';
import Api from '../../../services/api'; // apiurl
import {API_URL} from './url';

const ImageContainer = ({newImage}) =>{
    const [images, setImages] = useState([]);
    const [fallback, setFallback] = useState('');
    const getImages = async () =>{
        try {
            //const res = await fetch(`http://localhost:3001/posts`);
            const res = await axios.get(API_URL + 'api/images');
            if( !res.data.files){
                setFallback(res.data.msg);
                return
            } else{
                setImages(res.data.files);
            }
        } catch (error) {
            console.log("erro");
        }
    }
    useEffect(()=>{
        getImages();
    }, [newImage]);

    const configureImage = image =>{
        return 'http://localhost:3001/posts' + image;
    } 
    console.log(images);

    return(
        <div>
            {images.length > 0 ? (
                images.map(image =>(
                    <img src={configureImage(image)} key={image} width="200" height="200" className="image"/>
                ))
            ) 
        :
        <>
        <h1>{fallback}</h1>
        <hr/>
        <h3>lets go</h3>
        </>
        }
        </div>
    )
}

export default ImageContainer;
